export default {
  name: 'blog',
  components: {
    BlogNav: BlogNav,
    BlogFeed: BlogFeed,
    BlogPost: BlogPost,
    BlogFooter: BlogFooter
  },
  resource: 'Blog',
  props: {
    post: String,
    author: String
  },
  data: function data() {
    return {
      navs: 0,
      title: '',
      labels: {
        post: '',
        author: ''
      }
    };
  },
  computed: {
    content: function content() {
      return {
        title: this.title,
        labels: this.labels
      };
    },
    filters: function filters() {
      var filters = {};
      if (this.post) filters.post = this.post;
      if (this.author) filters.author = this.author;
      return filters;
    }
  },
  watch: {
    '$route.name': function $routeName(to, from) {
      if (to !== from) this.navs++;
    }
  },
  mounted: function mounted() {
    this.$getResource('blog').then(function (x) {// use pace hook to know when rendering is ready
    });
  }
};
