header {
  width: 100%;
  position: fixed;
  top: 0;
  height: 50px;
  z-index: 10;
  min-width: 320px;
  font-family: "Pathway Gothic One", sans-serif;
  text-transform: uppercase;
  transition: all 150ms ease 0ms;
}

.zoom-overlay-open header {
  transform: translateY(-100px);
}

.contact.zoom-overlay-open header {
  transform: translateY(0px);
  background: white;
  box-shadow: 0 0 5px 0 rgba(0, 0, 0, 0.15);
}

@media (min-width: 783px) {
  .admin-bar header {
    top: 32px;
  }
}

header .container {
  position: relative;
  height: 100%;
  max-width: none;
}

header .container .column {
  height: 100%;
}

header a {
  background: transparent;
  text-shadow: none !important;
}

header .logo {
  width: 20.3205rem;
  height: 3.0138rem;
  position: absolute;
  left: 0;
  top: 50%;
  margin-top: -1.4rem;
  display: block;
  color: #fff;
  font-size: 0;
  text-transform: none;
  font-size: 4.5rem;
  text-decoration: none;
}


header .logo .grain-mortar,
header .logo .ampersand {
  transition: all 150ms ease 0ms;
}

header .logo .grain-mortar *,
header .logo .ampersand * {
  fill: #333E48;
}

header .logo .grain-mortar {
  fill: #333E48;
}

header .logo .ampersand {
  fill: #DAA25A;
}

@media (max-width: 499px) {
  header .logo {
    width: 24px;
  }
  header .logo .grain-mortar {
    display: none;
  }
  header .logo .ampersand {
    transform: translateX(-90px);
  }
  header .logo svg {
    width: 200px;
  }
}

header .hamburger {
  position: absolute;
  right: -4vw;
  z-index: 9;
}

header nav {
  display: block;
  position: fixed;
  padding-top: 50px;
  right: 0;
  margin: 0;
  max-width: 320px;
  width: 70vw;
  height: 100vh;
  transform: translate3d(111%, 0%, 0px);
  transition: all 200ms cubic-bezier(0.61, 0.92, 0.68, 1.14) 0s;
  background: white;
  box-shadow: 0 0 30px 0 rgba(0, 0, 0, 0.25);
  z-index: 8;
}

header nav ul {
  list-style-type: none;
  padding: 0;
  margin: 0;
}

header nav a {
  color: #333E48;
  font-size: 2.6rem;
  letter-spacing: 0.15em;
  font-weight: 300;
  padding: 0.8em 5vw 0.8em 1em;
  display: block;
  opacity: 0;
  transform: translate3d(-6.25%, 0, 0);
  transition: all 250ms cubic-bezier(0.2, 0.3, 0.25, 0.9) 0s;
}

header nav a:hover, header nav a:focus {
  background: white;
}

.nav-open header nav {
  transform: translate3d(40px, 0, 0);
}

.nav-open header nav li:nth-of-type(1) a {
  transition-delay: 0.3s;
}

.nav-open header nav li:nth-of-type(2) a {
  transition-delay: 0.35s;
}

.nav-open header nav li:nth-of-type(3) a {
  transition-delay: 0.4s;
}

.nav-open header nav li:nth-of-type(4) a {
  transition-delay: 0.45s;
}

.nav-open header nav li:nth-of-type(5) a {
  transition-delay: 0.5s;
}

.nav-open header nav li:nth-of-type(6) a {
  transition-delay: 0.55s;
}

.nav-open header nav li:nth-of-type(7) a {
  transition-delay: 0.6s;
}

.nav-open header nav a {
  opacity: 1;
  transform: translate3d(0, 0, 0);
}

header.mini {
  background: white;
  height: 50px;
  box-shadow: 0 0 5px 0 rgba(0, 0, 0, 0.15);
}

header.nav-up:not(.article_complete) {
  transform: translateY(-48px);
}

@media (min-width: 1100px) {
  header.nav-up:not(.article_complete) {
    transform: translateY(-78px);
  }
}

.zoom-overlay-open.single-work header, .zoom-overlay-open.single-blog header {
  transform: translateY(-48px);
}

@media (min-width: 1100px) {
  .zoom-overlay-open.single-work header, .zoom-overlay-open.single-blog header {
    transform: translateY(-78px);
  }
}

@media (min-width: 1100px) {
  header {
    height: 95px;
    transition: all 200ms ease 0ms;
  }
  header .hamburger {
    display: none;
  }
  header::before {
    display: none;
  }
  header nav {
    box-shadow: none;
    display: block;
    position: absolute;
    top: 0;
    right: 5px;
    width: auto;
    background: transparent;
    height: 100%;
    overflow: initial;
    -webkit-overflow-scrolling: initial;
    transform: none;
    transition: none;
    padding: 0;
    max-width: 800px;
  }
  header nav ul {
    width: 100%;
    height: 100%;
    border: 0;
  }
  header nav ul > li {
    display: inline-block;
    padding-right: 5px;
    padding-left: 5px;
  }
  header nav ul > li:first-of-type {
    padding-left: 0;
  }
  header nav ul > li:last-of-type {
    padding-right: 0;
  }
  header nav ul li {
    border: none;
    position: relative;
    height: 100%;
  }
  header nav ul li.first-active ul::after {
    border-bottom: 15px solid #DAA25A;
  }
  header nav a {
    height: 100%;
    padding: 20px 8px;
    color: #333E48;
    opacity: 1;
    padding: 1em 0.6em;
    font-size: 2.1rem;
    font-weight: 500;
    display: inline-flex;
    align-items: center;
    align-content: center;
    transition: none;
  }
  header nav a:hover, header nav a:focus {
    background: none;
    color: #DAA25A;
  }
  header.mini {
    height: 80px;
  }
  header.mini nav a {
    color: #333E48;
    transition: background 150ms ease 0ms;
  }
}

@media (min-width: 1401px) {
  header nav a {
    padding: 1em 0.8em;
  }
}
