.developer {
  &.hero {
    background-repeat: no-repeat;
    background-size: cover;
    background-position: center;
    box-sizing: border-box;
    color: color(white);
    height: 100%;
    text-align: center;
    width: 100%;
    background-image: url(../assets/images/hero-image-400x800.jpg);
    height: 800px;

    @media (min-width: 600px) {
      background-image: url(../assets/images/hero-image-900x800.jpg);

    }
    @media (min-width: 900px) {
      background-image: url(../assets/images/hero-image-1450x800.jpg);
    }
  }
  &.content {
    align-items: center;
    display: flex;
    height: 100%;
    justify-content: center;
  }
  .developer-links {
    margin-top: 3rem;
    display: flex;
    list-style: none;
    flex-wrap: wrap;
  }

  .heading {
    color: #fff;
  }
}
